function removeItem(removeurl, compareurl, id)
{
	var randNumber = Math.random();
	$j.ajax({
		url: removeurl+'?no-cache='+randNumber,
		method: "POST",		
		success:function(data){
				var data = $j.parseJSON(data);	
				var count = data['count'];

	        	$j("#compCheck_"+id).attr("onclick","addCompareItem('"+compareurl+"', '"+removeurl+"', '"+id+"')");
	        	$j("#compCheck_"+id).removeClass("active");
	        	$j('.hiddenCount').attr('id', count);				
				$j('#itemCount').html(count);
		}		
	});
}

function addCompareItem(compareurl, removeurl, id)
{
	var randNumber = Math.random();
    $j.ajax({
		url: compareurl+'?no-cache='+randNumber,
		method: "POST",
		beforeSend:function(){
			var comp_count = parseInt($j('.hiddenCount').attr('id'));
			if(comp_count==4){
		    	$j("#compareModal").trigger( "click" );
		    	return false;
		    }
		},
		success:function(data){
				var data = $j.parseJSON(data);	
				var count = data['count'];
				   
	            $j("#compCheck_"+id).attr("onclick","removeItem('"+removeurl+"', '"+compareurl+"', '"+id+"')");
	            $j("#compCheck_"+id).addClass("active");
	            $j('.hiddenCount').attr('id', (count));
	            $j('#itemCount').html(count);
	        }		
	});
}


